mdui.updateTables = function (
  selector?: Selector | HTMLElement | ArrayLike<HTMLElement>,
): void {
  const $elements = isUndefined(selector) ? $('.mdui-table') : $(selector);

  $elements.each((_, element) => {
    const $element = $(element);
    const instance = $element.data(dataName);

    if (instance) {
      instance.init();
    } else {
      $element.data(dataName, new Table($element));
    }
  });
};
/**
 * touch  500ms  mousedown 
 *
 *  mousedown -> mouseup -> click
 *  touchstart -> touchend -> mousedown -> mouseup -> click
 *
 *  TouchHandler.isAllow(event) 
 *  touchstart  touchmovetouchendtouchcancel
 *
 * (function () {
 *   $document
 *     .on(start, function (e) {
 *       if (!isAllow(e)) {
 *         return;
 *       }
 *       register(e);
 *       console.log(e.type);
 *     })
 *     .on(move, function (e) {
 *       if (!isAllow(e)) {
 *         return;
 *       }
 *       console.log(e.type);
 *     })
 *     .on(end, function (e) {
 *       if (!isAllow(e)) {
 *         return;
 *       }
 *       console.log(e.type);
 *     })
 *     .on(unlock, register);
 * })();
 */

const startEvent = 'touchstart mousedown';
const moveEvent = 'touchmove mousemove';
const endEvent = 'touchend mouseup';
const cancelEvent = 'touchcancel mouseleave';
const unlockEvent = 'touchend touchmove touchcancel';

let touches = 0;

/**
 * 
 *  touch 
 * @param event
 */
function isAllow(event: Event): boolean {
  return !(
    touches &&
    [
      'mousedown',
      'mouseup',
      'mousemove',
      'click',
      'mouseover',
      'mouseout',
      'mouseenter',
      'mouseleave',
    ].indexOf(event.type) > -1
  );
}

/**
 *  touchstart  touchmovetouchendtouchcancel 
 * @param event
 */
function register(event: Event): void {
  if (event.type === 'touchstart') {
    //  touch 
    touches += 1;
  } else if (
    ['touchmove', 'touchend', 'touchcancel'].indexOf(event.type) > -1
  ) {
    // touch  500ms 
    setTimeout(function () {
      if (touches) {
        touches -= 1;
      }
    }, 500);
  }
}

