const customAttr = 'mdui-drawer';

type OPTIONS = {
  target: string;
  overlay?: boolean;
  swipe?: boolean;
};

$(() => {
  mdui.mutation(`[${customAttr}]`, function () {
    const $element = $(this);
    const options = parseOptions(this, customAttr) as OPTIONS;
    const selector = options.target;
    // @ts-ignore
    delete options.target;

    const $drawer = $(selector).first();
    const instance = new mdui.Drawer($drawer, options);

    $element.on('click', () => instance.toggle());
  });
});
import { isUndefined } from 'mdui.jq/es/utils';
import PlainObject from 'mdui.jq/es/interfaces/PlainObject';

type Func = () => any;

const container: PlainObject<Func[]> = {};

/**
 * 
 * @param name 
 */
function queue(name: string): Func[];

/**
 * 
 * @param name 
 * @param func 
 */
function queue(name: string, func: Func): void;

function queue(name: string, func?: Func): void | Func[] {
  if (isUndefined(container[name])) {
    container[name] = [];
  }

  if (isUndefined(func)) {
    return container[name];
  }

  container[name].push(func);
}

/**
 * 
 * @param name 
 */
function dequeue(name: string): void {
  if (isUndefined(container[name])) {
    return;
  }

  if (!container[name].length) {
    return;
  }

  const func = container[name].shift()!;

  func();
}

