const DEFAULT_BUTTON: BUTTON = {
  text: '',
  bold: false,
  close: true,
  // eslint-disable-next-line @typescript-eslint/no-empty-function
  onClick: () => {},
};

const DEFAULT_OPTIONS: OPTIONS = {
  title: '',
  content: '',
  buttons: [],
  stackedButtons: false,
  cssClass: '',
  history: true,
  overlay: true,
  modal: false,
  closeOnEsc: true,
  destroyOnClosed: true,
  // eslint-disable-next-line @typescript-eslint/no-empty-function
  onOpen: () => {},
  // eslint-disable-next-line @typescript-eslint/no-empty-function
  onOpened: () => {},
  // eslint-disable-next-line @typescript-eslint/no-empty-function
  onClose: () => {},
